INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, phase, question_order) VALUES
('Quantos são os dons do Espírito Santo?', ARRAY['5', '6', '7', '8'], 2, 'São sete dons do Espírito Santo: sabedoria, entendimento, conselho, fortaleza, ciência, piedade e temor de Deus.', 'Fácil', 'Espírito Santo', 81, 1),
('O dom da sabedoria nos dá:', ARRAY['Conhecimento das coisas', 'Gosto pelas coisas divinas', 'Força para agir', 'Capacidade de ensinar'], 1, 'O dom da sabedoria nos dá o gosto e o conhecimento das coisas divinas e eternas.', 'Médio', 'Espírito Santo', 81, 2),
('O dom do entendimento nos ajuda a:', ARRAY['Compreender as verdades da fé', 'Dar conselhos', 'Ter coragem', 'Amar a Deus'], 0, 'O dom do entendimento nos ajuda a compreender profundamente as verdades da fé.', 'Médio', 'Espírito Santo', 81, 3),
('O dom do conselho nos orienta para:', ARRAY['Estudar melhor', 'Tomar decisões certas', 'Ter paciência', 'Ser humilde'], 1, 'O dom do conselho nos orienta para tomar as decisões certas em nossa vida espiritual.', 'Médio', 'Espírito Santo', 81, 4),
('O dom da fortaleza nos dá:', ARRAY['Força física', 'Coragem espiritual', 'Inteligência', 'Paciência'], 1, 'O dom da fortaleza nos dá coragem espiritual para enfrentar dificuldades e tentações.', 'Fácil', 'Espírito Santo', 81, 5),
('O dom da ciência nos ensina:', ARRAY['Matemática', 'Como usar as criaturas', 'História', 'Geografia'], 1, 'O dom da ciência nos ensina como usar corretamente as criaturas para chegar a Deus.', 'Médio', 'Espírito Santo', 81, 6),
('O dom da piedade nos torna:', ARRAY['Estudiosos', 'Filhos de Deus', 'Corajosos', 'Sábios'], 1, 'O dom da piedade nos faz sentir como verdadeiros filhos de Deus e irmãos do próximo.', 'Médio', 'Espírito Santo', 81, 7),
('O dom do temor de Deus é:', ARRAY['Medo de Deus', 'Respeito filial a Deus', 'Terror', 'Angústia'], 1, 'O temor de Deus é um respeito filial e reverente a Deus, não medo, mas amor.', 'Médio', 'Espírito Santo', 81, 8),
('Os dons do Espírito Santo são recebidos:', ARRAY['No Batismo', 'Na Confirmação', 'Em ambos', 'Na Eucaristia'], 2, 'Os dons são recebidos no Batismo e aperfeiçoados na Confirmação.', 'Médio', 'Espírito Santo', 81, 9),
('Os dons do Espírito Santo nos ajudam a:', ARRAY['Ser ricos', 'Viver santamente', 'Ter sucesso', 'Ser famosos'], 1, 'Os dons do Espírito Santo nos ajudam a viver santamente e agradar a Deus.', 'Fácil', 'Espírito Santo', 81, 10);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, phase, question_order) VALUES
('Quantos são os frutos do Espírito Santo segundo São Paulo?', ARRAY['7', '9', '12', '15'], 1, 'São Paulo enumera 9 frutos do Espírito Santo em Gálatas 5,22-23.', 'Médio', 'Espírito Santo', 82, 1),
('O primeiro fruto do Espírito Santo é:', ARRAY['Paz', 'Alegria', 'Caridade', 'Paciência'], 2, 'O primeiro fruto é a caridade (amor), fonte de todos os outros frutos.', 'Médio', 'Espírito Santo', 82, 2),
('A alegria espiritual vem de:', ARRAY['Diversões', 'União com Deus', 'Riquezas', 'Fama'], 1, 'A alegria espiritual vem da união com Deus e da prática da virtude.', 'Fácil', 'Espírito Santo', 82, 3),
('A paz do Espírito Santo é:', ARRAY['Ausência de guerra', 'Tranquilidade da alma', 'Silêncio', 'Descanso'], 1, 'A paz espiritual é a tranquilidade da alma que vem de Deus.', 'Médio', 'Espírito Santo', 82, 4),
('A paciência nos ajuda a:', ARRAY['Dormir bem', 'Suportar as dificuldades', 'Correr rápido', 'Estudar mais'], 1, 'A paciência nos ajuda a suportar as dificuldades com serenidade.', 'Fácil', 'Espírito Santo', 82, 5),
('A bondade nos leva a:', ARRAY['Ser ricos', 'Fazer o bem', 'Ser famosos', 'Viajar muito'], 1, 'A bondade nos leva a fazer o bem e ajudar o próximo.', 'Fácil', 'Espírito Santo', 82, 6),
('A benignidade é:', ARRAY['Doçura no trato', 'Força física', 'Inteligência', 'Velocidade'], 0, 'A benignidade é a doçura e amabilidade no trato com os outros.', 'Médio', 'Espírito Santo', 82, 7),
('A longanimidade significa:', ARRAY['Vida longa', 'Paciência duradoura', 'Memória boa', 'Força grande'], 1, 'A longanimidade é a paciência duradoura, que não se cansa de esperar.', 'Difícil', 'Espírito Santo', 82, 8),
('A mansidão nos torna:', ARRAY['Fracos', 'Humildes e dóceis', 'Tristes', 'Lentos'], 1, 'A mansidão nos torna humildes, dóceis e controlados em nossos ímpetos.', 'Médio', 'Espírito Santo', 82, 9),
('A fidelidade é:', ARRAY['Ser casado', 'Ser constante na palavra', 'Ter fé', 'Ir à igreja'], 1, 'A fidelidade é ser constante na palavra dada e nos compromissos assumidos.', 'Médio', 'Espírito Santo', 82, 10);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, phase, question_order) VALUES
('Quantas são as bem-aventuranças?', ARRAY['6', '7', '8', '9'], 2, 'São oito bem-aventuranças proclamadas por Jesus no Sermão da Montanha.', 'Fácil', 'Bem-aventuranças', 83, 1),
('A primeira bem-aventurança é:', ARRAY['Pobres de espírito', 'Mansos', 'Misericordiosos', 'Puros de coração'], 0, 'A primeira é "Bem-aventurados os pobres de espírito, porque deles é o Reino dos céus".', 'Médio', 'Bem-aventuranças', 83, 2),
('Os pobres de espírito são aqueles que:', ARRAY['Não têm dinheiro', 'Confiam só em Deus', 'São ignorantes', 'Vivem na rua'], 1, 'Os pobres de espírito são aqueles que confiam totalmente em Deus, não nas riquezas.', 'Médio', 'Bem-aventuranças', 83, 3),
('Os mansos herdarão:', ARRAY['Dinheiro', 'A terra', 'Casas', 'Carros'], 1, 'Jesus disse: "Bem-aventurados os mansos, porque herdarão a terra".', 'Fácil', 'Bem-aventuranças', 83, 4),
('Os que choram serão:', ARRAY['Consolados', 'Curados', 'Enrichecidos', 'Promovidos'], 0, 'Jesus promete: "Bem-aventurados os que choram, porque serão consolados".', 'Fácil', 'Bem-aventuranças', 83, 5),
('Os que têm fome e sede de justiça serão:', ARRAY['Alimentados', 'Saciados', 'Curados', 'Protegidos'], 1, 'Jesus promete que os que têm fome e sede de justiça serão saciados.', 'Médio', 'Bem-aventuranças', 83, 6),
('Os misericordiosos:', ARRAY['Serão ricos', 'Alcançarão misericórdia', 'Serão famosos', 'Viverão muito'], 1, 'Jesus ensina: "Bem-aventurados os misericordiosos, porque alcançarão misericórdia".', 'Fácil', 'Bem-aventuranças', 83, 7),
('Os puros de coração:', ARRAY['Serão santos', 'Verão a Deus', 'Serão sábios', 'Serão fortes'], 1, 'Jesus promete: "Bem-aventurados os puros de coração, porque verão a Deus".', 'Médio', 'Bem-aventuranças', 83, 8),
('Os pacificadores serão chamados:', ARRAY['Heróis', 'Filhos de Deus', 'Santos', 'Sábios'], 1, 'Jesus ensina: "Bem-aventurados os pacificadores, porque serão chamados filhos de Deus".', 'Médio', 'Bem-aventuranças', 83, 9),
('Os perseguidos por causa da justiça:', ARRAY['Serão vingados', 'Terão o Reino dos céus', 'Serão libertados', 'Serão consolados'], 1, 'Jesus promete o Reino dos céus aos perseguidos por causa da justiça.', 'Médio', 'Bem-aventuranças', 83, 10);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, phase, question_order) VALUES
('Quantas são as obras de misericórdia corporais?', ARRAY['5', '6', '7', '8'], 2, 'São sete obras de misericórdia corporais que atendem às necessidades físicas do próximo.', 'Médio', 'Misericórdia', 84, 1),
('A primeira obra de misericórdia corporal é:', ARRAY['Vestir os nus', 'Dar de comer aos famintos', 'Visitar os enfermos', 'Abrigar os desabrigados'], 1, 'A primeira obra é dar de comer aos famintos, atendendo à necessidade básica.', 'Fácil', 'Misericórdia', 84, 2),
('Dar de beber aos sedentos significa:', ARRAY['Só dar água', 'Atender necessidades básicas', 'Trabalhar em poços', 'Vender bebidas'], 1, 'Significa atender às necessidades básicas de água e outras bebidas necessárias.', 'Fácil', 'Misericórdia', 84, 3),
('Vestir os nus inclui:', ARRAY['Só roupas novas', 'Qualquer vestimenta necessária', 'Só uniformes', 'Só roupas caras'], 1, 'Inclui fornecer qualquer vestimenta necessária para cobrir e proteger o corpo.', 'Médio', 'Misericórdia', 84, 4),
('Abrigar os desabrigados significa:', ARRAY['Construir casas', 'Oferecer abrigo temporário', 'Ambas as anteriores', 'Trabalhar na construção'], 2, 'Pode ser tanto oferecer abrigo temporário quanto ajudar na construção de moradias.', 'Médio', 'Misericórdia', 84, 5),
('Visitar os enfermos é importante porque:', ARRAY['Ganhamos dinheiro', 'Consolamos e ajudamos', 'Aprendemos medicina', 'Ficamos famosos'], 1, 'É importante porque consolamos os doentes e os ajudamos em suas necessidades.', 'Fácil', 'Misericórdia', 84, 6),
('Visitar os presos demonstra:', ARRAY['Curiosidade', 'Caridade cristã', 'Interesse jurídico', 'Medo'], 1, 'Demonstra caridade cristã, lembrando que Jesus também foi preso injustamente.', 'Médio', 'Misericórdia', 84, 7),
('Enterrar os mortos é obra de misericórdia porque:', ARRAY['É lei', 'Dignifica a pessoa', 'É costume', 'É obrigatório'], 1, 'É obra de misericórdia porque dignifica a pessoa humana mesmo após a morte.', 'Médio', 'Misericórdia', 84, 8),
('As obras de misericórdia corporais baseiam-se em:', ARRAY['Tradição humana', 'Palavras de Jesus', 'Lei civil', 'Filosofia'], 1, 'Baseiam-se nas palavras de Jesus sobre o Juízo Final em Mateus 25.', 'Médio', 'Misericórdia', 84, 9),
('Praticar obras de misericórdia nos leva a:', ARRAY['Ficar ricos', 'Salvação eterna', 'Fama mundial', 'Poder político'], 1, 'Jesus promete a salvação eterna para quem pratica obras de misericórdia.', 'Fácil', 'Misericórdia', 84, 10);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, phase, question_order) VALUES
('Quantas são as obras de misericórdia espirituais?', ARRAY['5', '6', '7', '8'], 2, 'São sete obras de misericórdia espirituais que atendem às necessidades da alma.', 'Médio', 'Misericórdia', 85, 1),
('Ensinar os ignorantes significa:', ARRAY['Criticar quem não sabe', 'Transmitir conhecimento', 'Só ensinar na escola', 'Cobrar pelo ensino'], 1, 'Significa transmitir conhecimento, especialmente sobre a fé e a moral cristã.', 'Fácil', 'Misericórdia', 85, 2),
('Dar bom conselho aos que precisam é:', ARRAY['Interferir na vida alheia', 'Ajudar no discernimento', 'Impor nossa vontade', 'Julgar os outros'], 1, 'É ajudar as pessoas no discernimento e nas decisões importantes da vida.', 'Médio', 'Misericórdia', 85, 3),
('Corrigir os que erram deve ser feito com:', ARRAY['Dureza', 'Caridade', 'Raiva', 'Publicidade'], 1, 'A correção deve ser feita com caridade, humildade e no momento oportuno.', 'Médio', 'Misericórdia', 85, 4),
('Consolar os aflitos significa:', ARRAY['Dar dinheiro', 'Oferecer apoio espiritual', 'Resolver problemas', 'Dar presentes'], 1, 'Significa oferecer apoio espiritual e emocional a quem está sofrendo.', 'Fácil', 'Misericórdia', 85, 5),
('Perdoar as injúrias é:', ARRAY['Ser fraco', 'Imitar Jesus', 'Ser bobo', 'Perder dignidade'], 1, 'É imitar Jesus, que perdoou até mesmo seus executores na cruz.', 'Médio', 'Misericórdia', 85, 6),
('Suportar com paciência as fraquezas do próximo significa:', ARRAY['Ser passivo', 'Ter tolerância cristã', 'Aceitar tudo', 'Não reagir nunca'], 1, 'Significa ter tolerância cristã, reconhecendo nossas próprias limitações.', 'Médio', 'Misericórdia', 85, 7),
('Orar pelos vivos e mortos é importante porque:', ARRAY['É tradição', 'A oração tem poder', 'É obrigatório', 'É bonito'], 1, 'A oração tem poder e pode ajudar tanto os vivos quanto as almas do purgatório.', 'Médio', 'Misericórdia', 85, 8),
('A diferença entre obras corporais e espirituais é:', ARRAY['Não há diferença', 'Atendem corpo ou alma', 'Uma é mais importante', 'São para pessoas diferentes'], 1, 'As corporais atendem ao corpo, as espirituais atendem à alma e espírito.', 'Médio', 'Misericórdia', 85, 9),
('Praticar obras de misericórdia espirituais requer:', ARRAY['Dinheiro', 'Sabedoria e caridade', 'Estudos superiores', 'Autorização'], 1, 'Requer sabedoria para discernir e caridade para agir com amor.', 'Médio', 'Misericórdia', 85, 10);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, phase, question_order) VALUES
('O Advento dura quantas semanas?', ARRAY['3', '4', '5', '6'], 1, 'O Advento dura quatro semanas, preparando para o Natal.', 'Fácil', 'Liturgia', 86, 1),
('A cor litúrgica do Advento é:', ARRAY['Branco', 'Roxo', 'Verde', 'Vermelho'], 1, 'A cor do Advento é roxa, simbolizando penitência e preparação.', 'Fácil', 'Liturgia', 86, 2),
('O terceiro domingo do Advento é chamado de:', ARRAY['Gaudete', 'Laetare', 'Rorate', 'Veni'], 0, 'O terceiro domingo é chamado "Gaudete" (alegrai-vos) e usa cor rosa.', 'Difícil', 'Liturgia', 86, 3),
('A coroa do Advento tem quantas velas?', ARRAY['3', '4', '5', '6'], 1, 'A coroa do Advento tem quatro velas, uma para cada domingo.', 'Fácil', 'Liturgia', 86, 4),
('O tempo do Natal vai até:', ARRAY['31 de dezembro', 'Reis Magos', 'Batismo do Senhor', 'Candelária'], 2, 'O tempo do Natal vai até a festa do Batismo do Senhor.', 'Médio', 'Liturgia', 86, 5),
('A cor litúrgica do Natal é:', ARRAY['Branco', 'Dourado', 'Ambas', 'Verde'], 2, 'No Natal usam-se as cores branco e dourado, simbolizando alegria e glória.', 'Fácil', 'Liturgia', 86, 6),
('A Epifania celebra:', ARRAY['Nascimento de Jesus', 'Visita dos Magos', 'Batismo de Jesus', 'Apresentação no templo'], 1, 'A Epifania celebra a visita dos Reis Magos e a manifestação de Jesus.', 'Médio', 'Liturgia', 86, 7),
('A oitava do Natal termina em:', ARRAY['31 de dezembro', '1º de janeiro', '6 de janeiro', '2 de fevereiro'], 1, 'A oitava do Natal termina em 1º de janeiro, festa de Maria Mãe de Deus.', 'Médio', 'Liturgia', 86, 8),
('Durante o Advento, omite-se na Missa:', ARRAY['Glória', 'Aleluia', 'Credo', 'Pai Nosso'], 0, 'Durante o Advento omite-se o Glória, exceto em solenidades.', 'Médio', 'Liturgia', 86, 9),
('A antífona "O" do Advento são:', ARRAY['5', '6', '7', '8'], 2, 'São sete antífonas "O" cantadas nos últimos dias do Advento.', 'Difícil', 'Liturgia', 86, 10);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, phase, question_order) VALUES
('A Quaresma dura quantos dias?', ARRAY['30', '40', '50', '60'], 1, 'A Quaresma dura 40 dias, recordando os 40 dias de Jesus no deserto.', 'Fácil', 'Liturgia', 87, 1),
('A cor litúrgica da Quaresma é:', ARRAY['Branco', 'Roxo', 'Preto', 'Verde'], 1, 'A cor da Quaresma é roxa, simbolizando penitência e conversão.', 'Fácil', 'Liturgia', 87, 2),
('O quarto domingo da Quaresma é chamado de:', ARRAY['Gaudete', 'Laetare', 'Rorate', 'Veni'], 1, 'O quarto domingo é chamado "Laetare" (alegrai-vos) e pode usar cor rosa.', 'Difícil', 'Liturgia', 87, 3),
('Durante a Quaresma omite-se:', ARRAY['Glória', 'Aleluia', 'Ambos', 'Credo'], 2, 'Durante a Quaresma omitem-se o Glória e o Aleluia, exceto em solenidades.', 'Médio', 'Liturgia', 87, 4),
('A Semana Santa começa no:', ARRAY['Domingo de Ramos', 'Quinta-feira Santa', 'Sexta-feira Santa', 'Sábado Santo'], 0, 'A Semana Santa começa no Domingo de Ramos.', 'Fácil', 'Liturgia', 87, 5),
('O Tríduo Pascal compreende:', ARRAY['3 dias', 'Quinta, Sexta e Sábado Santos', '3 celebrações principais', 'Todas as anteriores'], 3, 'O Tríduo Pascal são as três celebrações: Quinta Santa, Sexta Santa e Vigília Pascal.', 'Médio', 'Liturgia', 87, 6),
('A Vigília Pascal é celebrada:', ARRAY['Sexta-feira Santa', 'Sábado Santo à noite', 'Domingo de Páscoa', 'Segunda-feira de Páscoa'], 1, 'A Vigília Pascal é celebrada na noite do Sábado Santo.', 'Médio', 'Liturgia', 87, 7),
('O tempo pascal dura:', ARRAY['7 dias', '40 dias', '50 dias', '60 dias'], 2, 'O tempo pascal dura 50 dias, até Pentecostes.', 'Médio', 'Liturgia', 87, 8),
('Durante o tempo pascal canta-se:', ARRAY['Glória', 'Aleluia', 'Te Deum', 'Todas as anteriores'], 3, 'Durante a Páscoa cantam-se Glória, Aleluia e outros cânticos de alegria.', 'Fácil', 'Liturgia', 87, 9),
('Pentecostes celebra:', ARRAY['Ressurreição', 'Descida do Espírito Santo', 'Ascensão', 'Transfiguração'], 1, 'Pentecostes celebra a descida do Espírito Santo sobre os apóstolos.', 'Fácil', 'Liturgia', 87, 10);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, phase, question_order) VALUES
('Quem foi o primeiro santo brasileiro?', ARRAY['Santo Antônio de Sant\'Ana Galvão', 'São José de Anchieta', 'Santa Paulina', 'Beato José de Anchieta'], 0, 'Santo Antônio de Sant\'Ana Galvão foi o primeiro santo nascido no Brasil, canonizado em 2007.', 'Médio', 'Santos Brasileiros', 88, 1),
('São José de Anchieta é conhecido como:', ARRAY['Apóstolo do Brasil', 'Fundador de cidades', 'Poeta religioso', 'Todas as anteriores'], 3, 'São José de Anchieta é conhecido por todos esses títulos por sua obra no Brasil.', 'Médio', 'Santos Brasileiros', 88, 2),
('Santa Paulina foi a primeira:', ARRAY['Mártir brasileira', 'Santa nascida no Brasil', 'Fundadora de congregação', 'Missionária'], 1, 'Santa Paulina (Amabile Visintainer) foi a primeira santa nascida no Brasil.', 'Médio', 'Santos Brasileiros', 88, 3),
('Frei Galvão é conhecido pelos:', ARRAY['Milagres', 'Pílulas de papel', 'Sermões', 'Escritos'], 1, 'Frei Galvão é famoso pelas "pílulas de papel" com orações que curavam.', 'Médio', 'Santos Brasileiros', 88, 4),
('São José de Anchieta ajudou a fundar:', ARRAY['Rio de Janeiro', 'São Paulo', 'Salvador', 'Brasília'], 1, 'São José de Anchieta participou da fundação de São Paulo em 1554.', 'Médio', 'Santos Brasileiros', 88, 5),
('A Beata Nhá Chica viveu em:', ARRAY['São Paulo', 'Rio de Janeiro', 'Minas Gerais', 'Bahia'], 2, 'A Beata Nhá Chica (Francisca de Paula de Jesus) viveu em Baependi, MG.', 'Médio', 'Santos Brasileiros', 88, 6),
('O Servo de Deus Padre Cícero era conhecido por:', ARRAY['Milagres eucarísticos', 'Obras sociais', 'Devoção popular', 'Todas as anteriores'], 3, 'Padre Cícero Romão Batista era conhecido por tudo isso no Ceará.', 'Médio', 'Santos Brasileiros', 88, 7),
('A Venerável Irmã Adélia trabalhou com:', ARRAY['Escolas', 'Hospitais', 'Orfanatos', 'Todas as anteriores'], 3, 'Irmã Adélia trabalhou em escolas, hospitais e orfanatos em São Paulo.', 'Difícil', 'Santos Brasileiros', 88, 8),
('O Beato Bartolomeu Fernandes dos Mártires era:', ARRAY['Franciscano', 'Jesuíta', 'Dominicano', 'Carmelita'], 0, 'Beato Bartolomeu era franciscano e missionário no Brasil colonial.', 'Difícil', 'Santos Brasileiros', 88, 9),
('Quantos santos canonizados o Brasil tem?', ARRAY['2', '3', '4', '5'], 1, 'O Brasil tem 3 santos canonizados: Frei Galvão, São José de Anchieta e Santa Paulina.', 'Médio', 'Santos Brasileiros', 88, 10);
INSERT INTO public.quiz_questions (question, options, correct_answer, explanation, difficulty, category, phase, question_order) VALUES
('Em Fátima, Nossa Senhora apareceu para:', ARRAY['Bernadette', 'Lúcia, Francisco e Jacinta', 'Juan Diego', 'Catherine Labouré'], 1, 'Em Fátima, Nossa Senhora apareceu para os três pastorinhos portugueses.', 'Fácil', 'Aparições', 89, 1),
('Quantas aparições houve em Fátima?', ARRAY['5', '6', '7', '8'], 1, 'Houve 6 aparições principais de Nossa Senhora em Fátima em 1917.', 'Médio', 'Aparições', 89, 2),
('O milagre do sol aconteceu em:', ARRAY['13 de maio', '13 de junho', '13 de outubro', '13 de dezembro'], 2, 'O milagre do sol aconteceu em 13 de outubro de 1917, na última aparição.', 'Médio', 'Aparições', 89, 3),
('Em Lourdes, Nossa Senhora se identificou como:', ARRAY['Mãe de Deus', 'Imaculada Conceição', 'Rainha do Céu', 'Mãe da Igreja'], 1, 'Em Lourdes, Nossa Senhora disse: "Eu sou a Imaculada Conceição".', 'Médio', 'Aparições', 89, 4),
('Bernadette Soubirous viu Nossa Senhora quantas vezes?', ARRAY['16', '17', '18', '19'], 2, 'Bernadette viu Nossa Senhora 18 vezes na gruta de Massabielle.', 'Difícil', 'Aparições', 89, 5),
